(1, 0, 54, 0, '197.2.12.237', 1380228281, 1, 0, 1, 0, 'حول الإسم', 'السلام عليكم ورحمة الله\n\nالرجاء إبراز إسمك ولقبك إن كنت أخ.. والتكني بكنية إن كنت أخت.. بارك الله فيك', '', 0, 0, '', 'sou5fcj2', 0, 0, 'u_72', '', 0),
(2, 0, 2, 0, '197.1.41.53', 1380525827, 1, 0, 1, 0, 'تغيير الإسم', 'السلام عليكم\n\nرجاءا إختيار كنية مثل أم... أم محمد، أم عبد الرحمن أو غير ذلك...\n\nلأن الأخوات سيكون لهن الحق في الدخول على المنتدى المخصص للأخوات.. وكذلك ليعرف الإخوة أنك أخت فيلتزموا حدودهم..\n\nبارك الله فيك\n\nإدارة المنتدى', '', 0, 0, '', '141afgn9', 0, 0, 'u_72', '', 0),
(3, 2, 72, 0, '41.228.41.8', 1380726072, 1, 0, 1, 0, 'Re: تغيير الإسم', 'السلام عليكم و رحمة لله و بركاته \nلو تفضلتم أريد أن أفهم لما تسمى الأخوات بكنية , علمت أن ذلك إتقاءا للفتنة و لكن لم أفهم ذلك جيدا. و لو تفضلتم بتغيير أسم المستخدم الخاص بي ب&quot; أمة لله &quot;. بارك لله فيكم', '', 0, 0, '', '2z2zod7a', 0, 0, 'u_2', '', 0),
(4, 2, 2, 0, '41.226.72.32', 1380828921, 1, 0, 1, 0, 'Re: تغيير الإسم', 'السلام عليكم\n\nإسم أمة الله موجود... رجاءا إختيار إسم آخر \n\nبارك الله فيك', '', 0, 0, '', '2zp36gug', 0, 0, 'u_72', '', 0),
(5, 2, 72, 0, '41.228.41.9', 1381077239, 1, 0, 1, 0, 'Re: تغيير الإسم', 'السلام  عليكم و رحمة لله و بركاته \n و السؤال ؟؟؟؟؟؟؟؟؟؟؟؟', '', 0, 0, '', '2nq2xtoz', 0, 0, 'u_2', '', 0),
